(function(){function init(){if($('asb_hide_column_left')){$('asb_hide_column_left').observe('click',toggle);}
if($('asb_hide_column_right')){$('asb_hide_column_right').observe('click',toggle);}}
function toggle(event){Event.stop(event);var position='left';if(this.id=='asb_hide_column_right'){position='right';}
var cookieName='asb_hide_'+position,column=$('asb_'+position+'_column_id'),closeIcon=$('asb_'+position+'_close'),openIcon=$('asb_'+position+'_open');var hide=Cookie.get(cookieName);if(hide==0||hide==undefined){column.style.display='none';closeIcon.style.display='none';openIcon.style.display='inline';Cookie.set(cookieName,1);}else{column.style.display='';closeIcon.style.display='inline';openIcon.style.display='none';Cookie.unset(cookieName);}}
Event.observe(window,'load',init);})();